import React, { Component } from 'react';
import Task from '../task/Task';
import '../../Globals.css';
import './Robot.css';

class Robot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      completed: 0,
      remaining: 5,
      score: 0
    }
  }


  taskCompleted = (points) => {
    let completed = this.state.completed;
    let remaining = this.state.remaining;
    let score = this.state.score;

    completed++;
    remaining--;
    // score = score + points;

    this.setState({
      completed: completed,
      remaining: remaining,
      // score: score
    });
  }

  renderTasks = () => {

    if(this.props.robot.tasks !== null) {
      let taskList = this.props.robot.tasks.map((task, index) => {
        return <Task taskCompleted={this.taskCompleted} key={index} task={task}/>
      });
      return taskList;

    } else {

      return null;
    }
  }

  render() {

    const type = (this.props.robot.type.toLowerCase()).replace(/^\w/, c => c.toUpperCase());

    return (
      <div className="Robot">
        <img src="/imgs/robot/deleteButton.png" alt="Delete Button" className="deleteBtn" onClick={() => this.props.deleteRobot(this.props.robot.id)}/>

        <div className="robotCard">
          <div className="leftSide">
            <img className="profileImg" alt="Robot Profile" src={`/imgs/robot/${this.props.robot.type}.png`} />

            <h1>{this.props.robot.name} - {type}</h1>
          </div>

        </div>

        <div className="dropdown">

          <div className="dropdownLeft">
            <h2>Tasks</h2>

            <div className="taskCountContainer">
              <p>Score: {this.state.score}</p>
              <p>Completed: {this.state.completed}</p>
              <p>Remaining: {this.state.remaining}</p>
            </div>
          </div>

          <div className="dropdownRight">
            <ul>
              {this.renderTasks()}
            </ul>          
          </div>

        </div>

      </div>
    );
  }
}

export default Robot;
