import React, { Component } from 'react';
import Button from './components/button/Button';
import Robot from './components/robot/Robot';
import Form from './components/form/Form';
import EditForm from './components/form/EditForm';
import '../../Globals.css';
import './Home.css';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showAddModal: false,
      showEditModal: false,      
    }
  }

  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form formData={this.state.formData} addRobot={this.addRobot} handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />

    } else if(this.state.showEditModal === true) {

       return <EditForm robot={this.state.selectedRobot}  editRobot={this.editRobot} handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />     
    }
  }


  render() {
    return (
      <div className="Home">
        <h1>Bot-O-Mat</h1>
        {this.props.renderModal()}

        <div className="contentContainer">
          <Button handleBtnClick={this.props.handleBtnClick} text="Add Robot"/>

          <div className="robotListContainer">
            {this.props.renderRobots()}
          </div>  
        </div>
      </div>
    );
  }
}

export default Home;
