import React, { Component } from 'react';
import '../../Globals.css';
import './Form.css';

class Form extends Component {

  render() {
    return (
      <div className="Form">

        <form>
          <input onClick={(e) => this.props.closeModal(e)} className="closeBtn">X</input>

          <fieldset>
            <legend>Create A Robot</legend>

            <p>
              <label htmlFor="name">Robot's Name:</label>
              <input type="text" id="name" name="robotName"/>
            </p>

            <p>
              <label htmlFor="type">Robot's Type:</label>
              <select id="type">
                <option value="Unipedal">Unipedal</option>
                <option value="Bipedal">Bipedal</option>
                <option value="Quadrupedal">Quadrupedal</option>
                <option value="Arachnid">Arachnid</option>
                <option value="Radial">Radial</option>
                <option value="Aeronautical">Aeronautical</option>
              </select>
            </p>
          </fieldset>
        </form>

      </div>
    );
  }
}

export default Form;
