import React, { Component } from 'react';
import Button from './components/button/Button';
import Robot from './components/robot/Robot';
import Form from './components/form/Form';
import './Globals.css';
import './App.css';

import tasks from '../tasks.json';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robotList: null,
      robotTypes: null,
      tasks: tasks,
      showAddModal: false,

      formData: {
        name: null,
        type: null
      }
    }
  }

  componentDidMount = () => {
    this.fetchRobots();
    this.fetchRobotTypes();
  };

  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form addRobot={this.addRobot} handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />
    } else {
      return null;
    }
  }

  handleBtnClick = () => {
    if(this.state.showAddModal === false) {
      this.setState({showAddModal: true});
    }
  }

  closeModal = (e) => {
    e.preventDefault();

    if(this.state.showAddModal === true) {
      this.setState({showAddModal: false});
    }
  }

  renderRobots = () => {
    if(this.state.robotList !== null) {
      const robotList = this.state.robotList.map((robot) => {
        return <Robot robot={robot} key={robot.name} />
      });
      return robotList;

    } else {
      return null;
    }
  }

  handleChange = (e, name) => {
    let formData = this.state.formData;

    formData[name] = e.target.value;
    this.setState({formData});
    console.log(this.state.formData);
  }

/*---------------------------*/
//            API
/*---------------------------*/

  fetchRobots = () => {
    const url = "http://localhost:5000/robots";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotList: resJSON});
      });
  }

  fetchRobotTypes = () => {
    const url = "http://localhost:5000/types";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotTypes: resJSON});
      });    
  }

  addRobot = () => {

    fetch('http://localhost:5000/robots', {
      method: 'POST',
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(this.state.formData)
    }).then((res) => {
      return res.json(); 
    }).then (() => {
      console.log("added robot");
    })

  }

/*---------------------------*/
//            
/*---------------------------*/

  render() {

    return (
      <div className="App">
        <h1>Bot-O-Mat</h1>

        {this.renderModal()}

        <div className="contentContainer">
          <Button handleBtnClick={this.handleBtnClick} text="Add Robot"/>

          <div className="robotListContainer">
            {this.renderRobots()}
          </div>  
        </div>
      </div>
    );
  }
}

export default App;
