import React, { Component } from 'react';
import Header from '../header/Header';
import Button from '../button/Button';
import Robot from '../robot/Robot';
import Form from '../form/Form';
import EditForm from '../form/EditForm';
import '../../Globals.css';
import './Home.css';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showAddModal: false,
      showEditModal: false,
      searchString: ""
    }
  }

  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form 
        formData={this.props.formData} 
        addRobot={this.props.addRobot} 
        handleChange={this.props.handleChange} 
        robotTypes={this.props.robotTypes} 
        closeModal={this.closeModal} 
      />

    } else if(this.state.showEditModal === true) {

       return <EditForm 
        robot={this.state.selectedRobot}  
        editRobot={this.props.editRobot} 
        handleChange={this.propshandleChange} 
        robotTypes={this.props.robotTypes} 
        closeModal={this.closeModal} 
       />     
    }
  }

  handleBtnClick = (formType, robot) => {
    if(formType === "add" && this.state.showAddModal === false) {
      this.setState({showAddModal: true});
    }

    if(formType === "edit" && this.state.showEditModal === false) {
      this.setState({
        showEditModal: true,
        selectedRobot: robot
      });
    }
  }

  closeModal = (e, formType) => {
    e.preventDefault();

    if(formType === "add" && this.state.showAddModal === true) {
      this.setState({showAddModal: false});
    } else if (formType === "edit" && this.state.showEditModal === true) {
      this.setState({showEditModal: false});
    }
  }

  renderRobots = () => {
    if(this.props.robotList !== null) {
      let robots = this.props.robotList.sort((a, b) => a.score < b.score);
      let search = this.state.searchString.trim().toLowerCase();  

      if(search.length > 0) {
        robots = robots.filter((robot) => {
          return robot.name.toLowerCase().match(search);
        });
        return robots.map((robot) => {
          return <Robot robots={robots} taskCompleted={this.props.taskCompleted} handleBtnClick={this.handleBtnClick} deleteRobot={this.props.deleteRobot} robot={robot} key={robot.id} />
        });

      } else {
        return robots.map((robot) => {
          return <Robot robots={robots} taskCompleted={this.props.taskCompleted} handleBtnClick={this.handleBtnClick} deleteRobot={this.props.deleteRobot} robot={robot} key={robot.id} />
        });
      }

    } else {
      return null;
    }
  }


  handleSearchChange = (e) => {
    this.setState({searchString: e.target.value});
  }

  render() {
    return (
      <div className="Home">
        <Header handleSearchChange={this.handleSearchChange} handleBtnClick={this.handleBtnClick}/>
        {this.renderModal()}

        <div className="contentContainer">

          <div className="robotIconContainer">
            <div className="robotCard">
              <img alt="Unipedal" src="/imgs/robot/Unipedal.png" />
              <p>Unipedal</p>
              <ul>
                <li>do the laundry</li>
                <li>take out the recycling</li>
                <li>mow the lawn</li>
                <li>rake the leaves</li>
                <li>give the dog a bath</li>
                <li>bake some cookies</li>
              </ul>
            </div>

            <div className="robotCard">
              <img alt="Unipedal" src="/imgs/robot/Bipedal.png" />
              <p>Bipedal</p>
              <ul>
                <li>do the dishes</li>
                <li>sweep the house</li>
                <li>take out the recycling</li>
                <li>make a sammich</li>
                <li>give the dog a bath</li>
                <li>wash the car</li>
              </ul>
            </div>

            <div className="robotCard">
              <img alt="Unipedal" src="/imgs/robot/Quadrupedal.png" />
              <p>Quadrupedal</p>
              <ul>
                <li>do the dishes</li>
                <li>sweep the house</li>
                <li>do the laundry</li>
                <li>take out the recycling</li>
                <li>bake some cookies</li>
                <li>wash the car</li>
              </ul>
            </div>

            <div className="robotCard">
              <img alt="Unipedal" src="/imgs/robot/Arachnid.png" />
              <p>Arachnid</p>
              <ul>
                <li>do the dishes</li>
                <li>make a sammich</li>
                <li>mow the lawn</li>
                <li></li>
                <li></li>
                <li></li>
              </ul>
            </div>

            <div className="robotCard">
              <img alt="Unipedal" src="/imgs/robot/Radial.png" />
              <p>Radial</p>
            </div>

            <div className="robotCard">
              <img alt="Unipedal" src="/imgs/robot/Aeronautical.png" />
              <p>Aeronautical</p>
            </div>                                                            
          </div>

          <div className="robotListContainer">
            {this.renderRobots()}
          </div>  

        </div>
      </div>
    );
  }
}

export default Home;
