import React, { Component } from 'react';
import Button from './components/button/Button';
import Robot from './components/robot/Robot';
import Form from './components/form/Form';
import './Globals.css';
import './App.css';

import robotData from '../robotList.json';
import robotTypes from '../robotTypes.json';
import tasks from '../tasks.json';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robotList: null,
      robotTypes: robotTypes,
      tasks: tasks,
      showAddModal: false
    }
  }

  componentDidMount = () => {
    this.fetchRobots();
  };

  fetchRobots = () => {
    const url = "http://localhost:5000/";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotList: resJSON});
      });
  }

  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form robotTypes={robotTypes} closeModal={this.closeModal} />
    } else {
      return null;
    }
  }

  handleBtnClick = () => {
    if(this.state.showAddModal === false) {
      this.setState({showAddModal: true});
    }
  }

  closeModal = (e) => {
    e.preventDefault();

    if(this.state.showAddModal === true) {
      this.setState({showAddModal: false});
    }
  }

  renderRobots = () => {
    if(this.state.robotList !== null) {
      const robotList = this.state.robotList.map((robot) => {
      return <Robot robot={robot} key={robot.name} />
      });
    }
  }

  // addRobot = (name, type) => {
  //   const robotList = this.state.robotList;

  //   const robot = {
  //     name: name,
  //     type: type
  //   }

  //   const newRobotList = robotList.push(...robotList, robot);

  //   console.log(newRobotList);
  // }


  render() {

    const robotList = this.state.robotList.map((robot) => {
      return <Robot robot={robot} key={robot.name} />
    });

    return (
      <div className="App">
        <h1>Bot-O-Mat</h1>

        {this.renderModal()}

        <div className="contentContainer">
          <Button handleBtnClick={this.handleBtnClick} text="Add Robot"/>

          <div className="robotListContainer">
            {robotList}
          </div>  
        </div>
      </div>
    );
  }
}

export default App;
