import React, { Component } from 'react';
import '../../Globals.css';
import './Form.css';

class Form extends Component {



  render() {
    const typeList = this.props.robotTypes.map((type, index) => {
      return <option key={index} value={type.value}>{type.name}</option>
    });

    return (
      <div className="Form">

        <form>
          <button onClick={(e) => this.props.closeModal(e)} className="closeBtn">X</button>

          <fieldset>
            <legend>Create A Robot</legend>

            <p>
              <label htmlFor="name">Robot's Name:</label>
              <input type="text" id="name" name="robotName"/>
            </p>

            <p>
              <label htmlFor="type">Robot's Type:</label>
              <select id="type">
                {typeList}
              </select>
            </p>
          </fieldset>

          <div className="btnContainer">
            <button className="createBtn">Create Robot</button>
          </div>

        </form>

      </div>
    );
  }
}

export default Form;
