import React, { Component } from 'react';
import Button from './components/button/Button';
import Robot from './components/robot/Robot';
import './Globals.css';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robotList: [
        {
          name: "robot1"
        }, 
        {
          name : "robot2"
        }
        ]
    }
  }



  render() {

    const robotList = this.state.robotList.map((robot) => {
      return <Robot key={robot.name} />
    })

    return (
      <div className="App">
        <h1>Bot-O-Mat</h1>

        <div className="contentContainer">
          <Button text="Add Robot"/>

          <div className="robotListContainer">
            {robotList}
          </div>  

        </div>
      </div>
    );
  }
}

export default App;
