import React, { Component } from 'react';
import '../../Globals.css';
import './Robot.css';

class Robot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: null,
      name: null,
      type: null,
      // tasks: []
    }
  }

  componentDidMount = () => {

    this.setState({
      id: this.props.robot.id,
      name: this.props.robot.name,
      type: this.props.robot.type
    });
  }

  handleClick = () => {
    console.log(this.state.id);
  }


  render() {
    // let taskList = this.props.robot.tasks.map((task, index) => {
    //   return (
    //     <li key={index}>
    //       <p>{task}</p>
    //       <div></div>
    //     </li>
    //   );
    // })

    return (
      <div className="Robot">

        <div className="leftColumn">
          <h2>{this.props.robot.name}</h2>
          <h3>{this.props.robot.type}</h3>

          <button>Edit Robot</button>
        </div>

        <div className="rightColumn">
          <button onClick={() => this.handleClick()}>X</button>

          <h2>Tasks</h2>

          <div className="taskCountContainer">
            <p>Completed: 0</p>
            <p>Remaining: 5</p>
          </div>

          <ul>
            {/* {taskList} */}
          </ul>
        </div>

      </div>
    );
  }
}

export default Robot;
