import React, { Component } from 'react';
import '../../Globals.css';
import './Task.css';

class Task extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isFinished: false
    }
  }

  componentDidMount = () => {
    setTimeout(this.completeTask, this.props.task.eta);
  }

  renderCheckmark = () => {
    if(this.state.isFinished === false) {
      return "/imgs/robot/uncheck.png";
    } else {
      return "/imgs/robot/check.png";
    }
  }

  completeTask = () => {
    this.setState({isFinished: true});

    this.props.taskCompleted(this.props.task.points);
  }

  render() {

    const description = (this.props.task.description.toLowerCase()).replace(/^\w/, c => c.toUpperCase());

    return (
      <li className="Task">
        <p className="desc">{description}</p>
        <p className="points">{this.props.task.points}pts</p>
        <img alt="Checkbox" src={this.renderCheckmark()}/>
      </li>
    );
  }
}

export default Task;
