import React, { Component } from 'react';
import Button from '../button/Button';
import Robot from '../robot/Robot';
import Form from '../form/Form';
import EditForm from '../form/EditForm';
import '../../Globals.css';
import './Home.css';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showAddModal: false,
      showEditModal: false,      
    }
  }

  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form formData={this.props.formData} addRobot={this.props.addRobot} handleChange={this.props.handleChange} robotTypes={this.props.robotTypes} closeModal={this.closeModal} />

    } else if(this.state.showEditModal === true) {

       return <EditForm 
       robot={this.state.selectedRobot}  editRobot={this.props.editRobot} handleChange={this.propshandleChange} robotTypes={this.props.robotTypes} closeModal={this.closeModal} 
       />     
    }
  }

  handleBtnClick = (formType, robot) => {
    if(formType === "add" && this.state.showAddModal === false) {
      this.setState({showAddModal: true});
    }

    if(formType === "edit" && this.state.showEditModal === false) {
      this.setState({
        showEditModal: true,
        selectedRobot: robot
      });
    }

  }

  closeModal = (e, formType) => {
    e.preventDefault();


    if(formType === "add" && this.state.showAddModal === true) {
      this.setState({showAddModal: false});
    } else if (formType === "edit" && this.state.showEditModal === true) {
      this.setState({showEditModal: false});
    }
  }

  renderRobots = () => {
    if(this.props.robotList !== null) {

      const robotList = this.props.robotList.map((robot) => {
        return <Robot handleBtnClick={this.handleBtnClick} deleteRobot={this.deleteRobot} robot={robot} key={robot.name} />
      });
      return robotList;

    } else {
      return null;
    }
  }



  render() {
    return (
      <div className="Home">
        <h1>Bot-O-Mat</h1>
        {this.renderModal()}

        <div className="contentContainer">
          <Button handleBtnClick={this.handleBtnClick} text="Add Robot"/>

          <div className="robotListContainer">
            {this.renderRobots()}
          </div>  
        </div>
      </div>
    );
  }
}

export default Home;
