import React, { Component } from 'react';
import Button from '../button/Button';
import Robot from '../robot/Robot';
import Form from '../form/Form';
import EditForm from '../form/EditForm';
import '../../Globals.css';
import './Home.css';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showAddModal: false,
      showEditModal: false,
      searchString: ""
    }
  }

  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form 
        formData={this.props.formData} 
        addRobot={this.props.addRobot} 
        handleChange={this.props.handleChange} 
        robotTypes={this.props.robotTypes} 
        closeModal={this.closeModal} 
      />

    } else if(this.state.showEditModal === true) {

       return <EditForm 
        robot={this.state.selectedRobot}  
        editRobot={this.props.editRobot} 
        handleChange={this.propshandleChange} 
        robotTypes={this.props.robotTypes} 
        closeModal={this.closeModal} 
       />     
    }
  }

  handleBtnClick = (formType, robot) => {
    if(formType === "add" && this.state.showAddModal === false) {
      this.setState({showAddModal: true});
    }

    if(formType === "edit" && this.state.showEditModal === false) {
      this.setState({
        showEditModal: true,
        selectedRobot: robot
      });
    }

  }

  closeModal = (e, formType) => {
    e.preventDefault();


    if(formType === "add" && this.state.showAddModal === true) {
      this.setState({showAddModal: false});
    } else if (formType === "edit" && this.state.showEditModal === true) {
      this.setState({showEditModal: false});
    }
  }

  renderRobots = () => {
    if(this.props.robotList !== null) {
      let robots = this.props.robotList;
      let search = this.state.searchString.trim().toLowerCase();  

      if(search.length > 0) {
        robots = robots.filter((robot) => {
          return robot.name.toLowerCase().match(search);
        });

        return robots.map((robot) => {
          return <Robot handleBtnClick={this.handleBtnClick} deleteRobot={this.props.deleteRobot} robot={robot} key={robot.id} />
        });

      } else {
        return this.props.robotList.map((robot) => {
          return <Robot handleBtnClick={this.handleBtnClick} deleteRobot={this.props.deleteRobot} robot={robot} key={robot.id} />
        });
      }


    } else {
      return null;
    }
  }

  handleSearchChange = (e) => {
    this.setState({searchString: e.target.value});
  }

  render() {
    return (
      <div className="Home">
        {this.renderModal()}

        <div className="robotIconContainer">
          <img src="/imgs/robot/Unipedal.png"/>
          <img src="/imgs/robot/Bipedal.png"/>
          <img src="/imgs/robot/Quadrupedal.png"/>
          <img src="/imgs/robot/Arachnid.png"/>
          <img src="/imgs/robot/Radial.png"/>
          <img src="/imgs/robot/Aeronautical.png"/>
        </div>

        <div className="userInputContainer">
          <Button handleBtnClick={this.handleBtnClick} text="Add Robot"/>

          <label htmlFor="search"></label>
          <input placeholder="Search" id="search" onChange={(e) => this.handleSearchChange(e)}/>
        </div>

        <div className="contentContainer">

          <div className="leftColumn">
            <div className="robotListContainer">
              {this.renderRobots()}
            </div>  
          </div>

          <div className="rightColumn">

          </div>

        </div>
      </div>
    );
  }
}

export default Home;
