import React, { Component } from 'react';
import '../../Globals.css';
import './Robot.css';

class Robot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robot: {
        id: null,
        name: null,
        type: null,
        isFinished: false,
        // tasks: []
      },
      dropdown: false
    }
  }

  componentDidMount = () => {

    this.setState({
      id: this.props.robot.id,
      name: this.props.robot.name,
      type: this.props.robot.type
    });
  }

  handleDropdownClick = () => {
    if(this.state.dropdown === false) {
      this.setState({dropdown: true})
    }
  }

  renderDropdown = () => {
    if(this.state.dropdown === true) {
      return "dropdown selected";
    } else {
      return "dropdown";
    }
  }

  renderCheckmark = () => {
    if(this.state.robot.isFinished === false) {
      return "/imgs/robot/uncheck.png";
    } else {
      return "/imgs/robot/check.png";
    }
  }

  render() {
    // let taskList = this.props.robot.tasks.map((task, index) => {
    //   return (
    //     <li key={index}>
    //       <p>{task}</p>
    //       <div></div>
    //     </li>
    //   );
    // })

    return (
      <div className="Robot">

        <div className="robotCard">
          <div className="leftSide">
            <h1>{this.state.name}</h1>
            <h2>{this.state.type}</h2>
          </div>

          <div className="rightSide">
            <img alt="Check Box" src={this.renderCheckmark()} />
          </div>
        </div>

        <div className={this.renderDropdown()}>

        </div>


{/*         
        <div className="leftColumn">
          <p>ID: {this.state.id}</p>
          <h2>{this.state.name}</h2>
          <h3>{this.state.type}</h3>


          <button onClick={() => this.props.handleBtnClick("edit", this.props.robot)}>Edit Robot</button>
        </div>

        <div className="rightColumn">
          <button className="deleteBtn" onClick={() => this.props.deleteRobot(this.state.id)}>X</button>

          <h2>Tasks</h2>

          <div className="taskCountContainer">
            <p>Completed: 0</p>
            <p>Remaining: 5</p>
          </div>

          <ul>
            {/* {taskList} */}
          {/* </ul>
        </div> */} 

      </div>
    );
  }
}

export default Robot;
