import React, { Component } from 'react';
import Home from './components/home/Home';
// import Button from './components/button/Button';
// import Robot from './components/robot/Robot';
// import Form from './components/form/Form';
// import EditForm from './components/form/EditForm';
import './Globals.css';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robotList: null,
      robotTypes: null,
      tasks: null,
      // showAddModal: false,
      // showEditModal: false,
      selectedRobot: null,

      fullRobots: null,

      formData: {
        name: "John Doe",
        type: "UNIPEDAL"
      }
    }
  }

  componentWillMount = () => {
    this.fetchRobots();
    this.fetchRobotTypes();
    this.fetchTasks();

    this.fetchFullRobot();
  };

  generateId = () => {
    return Math.floor(Math.random() * 10000) + 1;
  }


  // addRobotToState = (newRobot) => {
  //   let updatedRobots = [...this.state.robotList, newRobot];
  //   this.setState({robotList: updatedRobots});
  // }

  updateRobotsInState = (updatedRobot) => {
    const index = this.state.robotList.findIndex(robot => robot.id === updatedRobot.id);    

    const robotList = [...this.state.robotList];
    robotList[index] = updatedRobot;

    this.setState({robotList});    
  }

  handleChange = (e, name) => {
    let formData = this.state.formData;

    formData[name] = e.target.value;
    this.setState({formData});
    console.log(this.state.formData);
  }

  // renderModal = () => {
  //   if(this.state.showAddModal === true) {
  //     return <Form formData={this.state.formData} addRobot={this.addRobot} handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />

  //   } else if(this.state.showEditModal === true) {

  //      return <EditForm robot={this.state.selectedRobot}  editRobot={this.editRobot} handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />     
  //   }
  // }

  // handleBtnClick = (formType, robot) => {
  //   if(formType === "add" && this.state.showAddModal === false) {
  //     this.setState({showAddModal: true});
  //   }

  //   if(formType === "edit" && this.state.showEditModal === false) {
  //     this.setState({
  //       showEditModal: true,
  //       selectedRobot: robot
  //     });
  //   }

  // }

  // closeModal = (e, formType) => {
  //   e.preventDefault();


  //   if(formType === "add" && this.state.showAddModal === true) {
  //     this.setState({showAddModal: false});
  //   } else if (formType === "edit" && this.state.showEditModal === true) {
  //     this.setState({showEditModal: false});
  //   }
  // }

  // renderRobots = () => {
  //   if(this.state.robotList !== null) {

  //     const robotList = this.state.robotList.map((robot) => {
  //       return <Robot handleBtnClick={this.handleBtnClick} deleteRobot={this.deleteRobot} robot={robot} key={robot.name} />
  //     });
  //     return robotList;

  //   } else {
  //     return null;
  //   }
  // }

  // handleChange = (e, name) => {
  //   let formData = this.state.formData;

  //   formData[name] = e.target.value;
  //   this.setState({formData});
  //   console.log(this.state.formData);
  // }


/*---------------------------*/
//         API CALLS
/*---------------------------*/


  fetchRobots = () => {
    const url = "http://localhost:5000/robots";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotList: resJSON});
      });
  }

  fetchFullRobot = () => {
    const url = "http://localhost:5000/robots/full";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        console.log(resJSON);
      });    
  }

/////////////////////
  fetchRobotTypes = () => {
    const url = "http://localhost:5000/types";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotTypes: resJSON});
      });    
  }

/////////////////////
  fetchTasks = () => {
    const url = "http://localhost:5000/tasks";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({tasks: resJSON});
      }).then(() => {
        // this.assignRobotTasks();
      }); 
  }

/////////////////////
  addRobot = () => {

    this.setState({})

    const bodyData = {
      id: this.generateId(),
      name: this.state.formData.name,
      type: this.state.formData.type
    };

    fetch("http://localhost:5000/robots", {
      method: 'POST',
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(bodyData)

    }).then((res) => {
      return res.json(); 

    }).then ((resJSON) => {
      this.assignRobotTasks(bodyData.id);
    });
  }

//////////////////
  assignRobotTasks = (robotId) => {
    const tasks = [];
    const url = "http://localhost:5000/tasks/random";

    fetch(url)
      .then((res) => {
        return res.json();
      }).then((resJSON) => {

        resJSON.forEach(task => {
          tasks.push(task);
        });
      }).then(() => {

        for(let i = 0; i < 5; i++) {
          // const currentDate = new Date();
          // const time = `${currentDate.getHours()}${currentDate.getMinutes()}${currentDate.getSeconds()}`;

          const bodyData = {
            robotId: robotId,
            taskId: tasks[i].id,
            completed: 0,
            timeAssigned: 222222,
            timeFinished: 0,
            elapsedTime: null
          };

          console.log("BEFORE FETCH");
          fetch("http://localhost:5000/robots/full", {
                method: 'POST',
                headers: {
                  "Accept": "application/json",
                  "Content-Type": "application/json"
                },
                body: JSON.stringify(bodyData)

              }).then((res) => {
                console.log("INSIDE FETCH");
                return res.json(); 

              });
        }
          console.log("AFTER FETCH");
      })
  }

/////////////////////
  getFullRobots = () => {
   const url = "http://localhost:5000/robots/full";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotList: resJSON});
      });    
  }


/////////////////////
  editRobot = (id) => {
    if(id !== null) {
      const url = `http://localhost:5000/robots/${id}`;

      fetch(url, {
        method: 'PUT',
        headers: {
          "Accept": "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(this.state.formData)
      }).then(res => {
        return res.json();
      }).then((resJSON) => {
        this.updateRobotsInState(resJSON);
      });

    }
  }



/////////////////////
  deleteRobot = (id) => {
    if(id !== null) {
      const url = `http://localhost:5000/robots/${id}`;

      fetch(url, {
        method: 'DELETE',
      }).then(res => {
        console.log("Deleted Robot");
        return res.json();

      }).then(() => {
        const newRobotList = this.state.robotList.filter(robot => robot.id !== id);

        this.setState({robotList: newRobotList});
      });
    }
  }

/*---------------------------*/
//            
/*---------------------------*/





  render() {

    return (
      <div className="App">

        <Home 
          formData={this.state.formData} 
          robotList={this.state.robotList} 
          robotTypes={this.state.robotTypes}
          renderRobots={this.renderRobots} 
          renderModal={this.renderModal} 
          handleChange={this.handleChange}
          addRobot={this.addRobot}
          deleteRobot={this.deleteRobot}
        />
      </div>
    );
  }
}

export default App;
