import React, { Component } from 'react';
import Button from './components/button/Button';
import Robot from './components/robot/Robot';
import Form from './components/form/Form';
import EditForm from './components/form/EditForm';
import './Globals.css';
import './App.css';

import { fetchRobots } from './api';


class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robotList: null,
      robotTypes: null,
      tasks: null,
      showAddModal: false,
      showEditModal: false,
      lastId: null,

      formData: {
        name: "John Doe",
        type: "UNIPEDAL"
      }
    }
  }

  componentWillMount = () => {
    this.getFetchRobots();
    this.fetchRobotTypes();
    this.fetchTasks();
  };


  generateId = () => {
    return Math.floor(Math.random() * 10000) + 1;
  }


  renderModal = () => {
    if(this.state.showAddModal === true) {
      return <Form formData={this.state.formData} addRobot={this.addRobot} handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />

    } else if(this.state.showEditModal === true) {

       return <EditForm handleChange={this.handleChange} robotTypes={this.state.robotTypes} closeModal={this.closeModal} />     
    }
  }

  handleBtnClick = (formType) => {
    if(formType === "add" && this.state.showAddModal === false) {
      this.setState({showAddModal: true});
    }

    if(formType === "edit" && this.state.showEditModal === false) {
      this.setState({showEditModal: true});
    }
  }

  closeModal = (e, formType) => {
    e.preventDefault();


    if(formType === "add" && this.state.showAddModal === true) {
      this.setState({showAddModal: false});
    } else if (formType === "edit" && this.state.showEditModal === true) {
      this.setState({showEditModal: false});
    }
  }

  renderRobots = () => {
    // console.log(this.state.robotList);
    // if(this.state.robotList !== null) {

    //   const robotList = this.state.robotList.map((robot) => {
    //     return <Robot handleBtnClick={this.handleBtnClick} editRobot={this.editRobot} deleteRobot={this.deleteRobot} robot={robot} key={robot.name} />
    //   });
    //   return robotList;

    // } else {
    //   return null;
    // }
  }

  handleChange = (e, name) => {
    let formData = this.state.formData;

    formData[name] = e.target.value;
    this.setState({formData});
    console.log(this.state.formData);
  }


/*---------------------------*/
//         API CALLS
/*---------------------------*/




  getFetchRobots = () => {
    const data = fetchRobots();
    // console.log(data);
    this.setState({robotList: data});
  }

/////////////////////
  fetchRobotTypes = () => {
    const url = "http://localhost:5000/types";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotTypes: resJSON});
      });    
  }

/////////////////////
  fetchTasks = () => {
    const url = "http://localhost:5000/tasks";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({tasks: resJSON});
      }).then(() => {
        // this.assignRobotTasks();
      }); 
  }

/////////////////////
  addRobot = () => {

    const bodyData = {
      id: this.generateId(),
      name: this.state.formData.name,
      type: this.state.formData.type
    };

    fetch("http://localhost:5000/robots", {
      method: 'POST',
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(bodyData)

    }).then((res) => {
      return res.json(); 

    }).then (() => {
      this.assignRobotTasks(bodyData.id);
    });
  }

//////////////////
  assignRobotTasks = (robotId) => {
    const tasks = [];
    const url = "http://localhost:5000/tasks/random";

    fetch(url)
      .then((res) => {
        return res.json();
      }).then((resJSON) => {

        resJSON.forEach(task => {
          tasks.push(task);
        });
      }).then(() => {

        for(let i = 0; i < 5; i++) {
          const currentDate = new Date();
          const time = `${currentDate.getHours()}${currentDate.getMinutes()}${currentDate.getSeconds()}`;

          const bodyData = {
            robotId: robotId,
            taskId: tasks[i].id,
            completed: 0,
            timeAssigned: time,
            timeFinished: 0,
            elapsedTime: null
          };

          return fetch("http://localhost:5000/robots/full", {
                method: 'POST',
                headers: {
                  "Accept": "application/json",
                  "Content-Type": "application/json"
                },
                body: JSON.stringify(bodyData)

              }).then((res) => {
                console.log("IM HERE!!!");
                return res.json(); 

              });
        }
      }).then((res) => {
        console.log(res);
      })
  }

/////////////////////
  getFullRobots = () => {
   const url = "http://localhost:5000/robots/full";

    fetch(url)
      .then((res) => res.json())
      .then((resJSON) => {
        this.setState({robotList: resJSON});
      });    
  }


/////////////////////
  editRobot = () => {

  }



/////////////////////
  deleteRobot = (id) => {
    if(id !== null) {
      const url = `http://localhost:5000/robots/${id}`;

      fetch(url, {
        method: 'DELETE',
      }).then(res => {
        console.log("Deleted Robot");
        return res.json();

      }).then(() => {
        const newRobotList = this.state.robotList.filter(robot => robot.id !== id);

        this.setState({robotList: newRobotList});
      });
    }
  }

/*---------------------------*/
//            
/*---------------------------*/





  render() {

    return (
      <div className="App">
        <h1>Bot-O-Mat</h1>

        {this.renderModal()}

        <div className="contentContainer">
          <Button handleBtnClick={this.handleBtnClick} text="Add Robot"/>

          <div className="robotListContainer">
            {this.renderRobots()}
          </div>  
        </div>
      </div>
    );
  }
}

export default App;
