import React, { Component } from 'react';
import '../../Globals.css';
import './Robot.css';

class Robot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      robot: {
        // id: null,
        // name: null,
        // type: null,
        // // tasks: []
      }
    }
  }

  componentDidMount = () => {

    this.setState({
      robot: this.props.robot
      // id: this.props.robot.id,
      // name: this.props.robot.name,
      // type: this.props.robot.type
    });
  }

  render() {
    // let taskList = this.props.robot.tasks.map((task, index) => {
    //   return (
    //     <li key={index}>
    //       <p>{task}</p>
    //       <div></div>
    //     </li>
    //   );
    // })

    return (
      <div className="Robot">

        <div className="leftColumn">
          <h2>{this.state.name}</h2>
          <h3>{this.state.type}</h3>
          <p>{this.state.id}</p>

          <button onClick={() => this.props.handleBtnClick("edit", this.state.id)}>Edit Robot</button>
        </div>

        <div className="rightColumn">
          <button className="deleteBtn" onClick={() => this.props.deleteRobot(this.state.id)}>X</button>

          <h2>Tasks</h2>

          <div className="taskCountContainer">
            <p>Completed: 0</p>
            <p>Remaining: 5</p>
          </div>

          <ul>
            {/* {taskList} */}
          </ul>
        </div>

      </div>
    );
  }
}

export default Robot;
