import React, { Component } from 'react';
import Task from '../task/Task';
import '../../Globals.css';
import './Robot.css';

class Robot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isFinished: false,
      completed: 0,
      remaining: 5
    }
  }


  taskCompleted = () => {
    let completed = this.state.completed;
    let remaining = this.state.remaining;

    completed++;
    remaining--;

    this.setState({
      completed: completed,
      remaining: remaining
    });

    if(completed === 5) {
      this.setState({isFinished: true});
    }
  }

  handleDropdownClick = () => {
    if(this.state.dropdown === false) {
      this.setState({dropdown: true});
    } else {
      this.setState({dropdown: false});
    }
  }

  renderTasks = () => {

    if(this.props.robot.tasks !== null) {
      let taskList = this.props.robot.tasks.map((task, index) => {
        return <Task taskCompleted={this.taskCompleted} key={index} task={task}/>
      });
      return taskList;

    } else {

      return null;
    }
  }

  renderDropdown = () => {
    if(this.state.dropdown === true) {
      return "dropdown selected";
    } else {
      return "dropdown";
    }
  }

  renderCheckmark = () => {
    if(this.state.isFinished === false) {
      return "/imgs/robot/uncheck.png";
    } else {
      return "/imgs/robot/check.png";
    }
  }

  render() {

    return (
      <div className="Robot">
        <img src="/imgs/robot/deleteButton.png" alt="Delete Button" className="deleteBtn" onClick={() => this.props.deleteRobot(this.props.robot.id)}/>

        <div className="robotCard"  onClick={() => this.handleDropdownClick()}>
          <div className="leftSide">
            <h1>{this.props.robot.name} - </h1>
            <h2>{this.props.robot.type}</h2>
          </div>

          <div className="rightSide">
            <img alt="Check Box" src={this.renderCheckmark()} />
          </div>
        </div>

        <div className={this.renderDropdown()}>
          <h2>Tasks</h2>

          <div className="taskCountContainer">
            <p>Completed: {this.state.completed}</p>
            <p>Remaining: {this.state.remaining}</p>
          </div>

          <ul>
            {this.renderTasks()}
          </ul>          
        </div>

      </div>
    );
  }
}

export default Robot;
